import React, { Component } from 'react';
function importAll(r) {
  return r.keys().map(r);
}

const images = importAll(require.context('./assets/', false, /\.(png|jpe?g|svg)$/));
class App extends Component {
	constructor(){
		super();
		this.state={
			imgList:["image1","image2", "image3", "image4", "image5", "image6", "image7", "image8"],
			currentImg:"image1.png"
		};
		this.handleChange=this.handleChange.bind(this);
	}
	handleChange(img){
		this.setState({currentImg:img});
	}
  render() {
    return (
      <div className="App">
		<div className="image_view">
			<img src={this.state.currentImg}></img>
		</div>
		<div>
		{this.state.imgList && this.state.imgList.map((item, idx) => {
				return(
					<img src={images[idx]} className="thumb_img" 	></img>
				)		
		})}
		</div>
      </div>	  
    );
  }
}

export default App;
