import React, { Component } from 'react';
function importAll(r) {
  return r.keys().map(r);
}

const images = importAll(require.context('./assets/', false, /\.(png|jpe?g|svg)$/));
class App extends Component {
	constructor(){
		super();
		this.state={
			currentImgIndex:"0"
		};
		this.handleChange=this.handleChange.bind(this);
	}
	handleChange(idx){
		this.setState({currentImg:idx});
	}
  render() {
    return (
      <div className="App">
		<div className="image_view">
			<img src={images[this.state.currentImgIndex]} className="img_view"></img>
		</div>
		<div className="textAlignCenter">
		{images && images.map((item, idx) => {
				return(
					<img key={idx} src={images[idx]} className={"thumb_img" + (this.state.currentImgIndex!=idx ? " opacity2" : "")} onClick={(e) => {this.handleChange(idx)}}></img>
				)		
		})}
		</div>
      </div>	  
    );
  }
}

export default App;
