import React, { Component } from 'react';
function importAll(r) {
  return r.keys().map(r);
}

const images = importAll(require.context('./assets/', false, /\.(png|jpe?g|svg)$/));
class App extends Component {
	constructor(){
		super();
		this.state={
			currentImgIndex:"0"
		};
	}	
  render() {
    return (
      <div className="App">
		<div className="image_view">
			<img src={images[this.state.currentImgIndex]} className="img_view"></img>
		</div>
		<div className="textAlignCenter">
		{images && images.map((item, idx) => {
				return(
					<div key={idx} onClick={() => this.setState({currentImg:idx})}><img key={idx} src={images[idx]} alt={images[idx]} className={"thumb_img" + (this.state.currentImgIndex!=idx ? " opacity2" : "")}></img></div>
				)		
		})}
		</div>
      </div>	  
    );
  }
}

export default App;
